import React, { Component } from 'react';
import Profile from './Profile';
import Education from './Forms/Education';
import Projects from './Projects';
import Experience from './Experience';
import Extras from './Forms/Extras';

export class Resume extends Component {
    state = {
        step: 1,

        // Profile page
        firstname: '',
        lastname: '',
        email: '',
        phone: '',
        github: '',
        linkedin: '',

        // Education page
        college: '',
        fromyear1: '',
        toyear1: '',
        qualification1: '',
        description1: '',

        school: '',
        fromyear2: '',
        toyear2: '',
        qualification2: '',
        description2: '',

        // Poject page
        title1: '',
        link1: '',
        projectDescription1: '',

        title2: '',
        link2: '',
        projectDescription2: '',

        title13: '',
        link3: '',
        projectDescription3: '',

        //Experience page
        institute1: '',
        position1: '',
        duration1: '',
        experienceDescription1: '',

        institute2: '',
        position2: '',
        duration2: '',
        experienceDescription2: '',

        //Extra page
        skill1: '',
        skill2: '',
        skill3: '',
        skill4: '',

        interest1: '',
        interest2: '',
        interest3: '',
        interest4: '',
    };

    nextStep = () => {
        const { step } = this.state;
        this.setState({
            step: step + 1,
        });
    };

    prevStep = () => {
        const { step } = this.state;
        this.setState({
            step: step - 1,
        });
    };

    handleChange = ({ target: { value, name } }) => {
        this.setState({ [name]: value });
    }

    render() {
        const { step } = this.state;
        const {
            // Profile page
            firstname,
            lastname,
            email,
            phone,
            github,
            linkedin,

            // Education page
            college,
            fromyear1,
            toyear1,
            qualification1,
            description1,

            school,
            fromyear2,
            toyear2,
            qualification2,
            description2,

            // Poject page
            title1,
            link1,
            projectDescription1,

            title2,
            link2,
            projectDescription2,

            title13,
            link3,
            projectDescription3,

            //Experience page
            institute1,
            position1,
            duration1,
            experienceDescription1,

            institute2,
            position2,
            duration2,
            experienceDescript,

            //Extra page
            skill1,
            skill2,
            skill3,
            skill4,

            interest1,
            interest2,
            interest3,
            interest4,
        } = this.state;

        const values = {
            // Profile page
            firstname,
            lastname,
            email,
            phone,
            github,
            linkedin,

            // Education page
            college,
            fromyear1,
            toyear1,
            qualification1,
            description1,

            school,
            fromyear2,
            toyear2,
            qualification2,
            description2,

            // Poject page
            title1,
            link1,
            projectDescription1,

            title2,
            link2,
            projectDescription2,

            title13,
            link3,
            projectDescription3,

            //Experience page
            institute1,
            position1,
            duration1,
            experienceDescription1,

            institute2,
            position2,
            duration2,
            experienceDescript,

            //Extra page
            skill1,
            skill2,
            skill3,
            skill4,

            interest1,
            interest2,
            interest3,
            interest4
        };

        switch (step) {
            case 1:
                return (
                    <div className="App mt-c">
                        <div className="container col-lg-10 mx-auto text-center">
                            <Profile 
                                nextStep={this.nextStep}
                                handleChange={this.handleChange}
                                values={values}
                            />
                        </div>
                    </div>
                );
            case 2:
                return (
                    <div className="App mt-3">
                        <div className="container col-lg-10 mx-auto text-center">
                            <Education 
                                nextStep={this.nextStep}
                                prevStep={this.prevStep}
                                handleChange={this.handleChange}
                                values={values}
                            />
                        </div>
                    </div>
                );
            case 3:
                return (
                    <div className="App mt-3">
                        <div className="container col-lg-8 mx-auto text-center">
                            <Projects 
                                nextStep={this.nextStep}
                                prevStep={this.prevStep}
                                handleChange={this.handleChange}
                                values={values}
                            />
                        </div>
                    </div>
                );
            case 4:
                return (
                    <div className="App mt-3">
                        <div className="container col-lg-10 mx-auto text-center">
                            <Experience 
                                nextStep={this.nextStep}
                                prevStep={this.prevStep}
                                handleChange={this.handleChange}
                                values={values}
                            />
                        </div>
                    </div>
                );
            case 5:
                return (
                    <div className="App mt-3">
                        <div className="container col-lg-10 mx-auto text-center">
                            <Extras 
                                prevStep={this.prevStep}
                                handleChange={this.handleChange}
                                values={values}
                            />
                        </div>
                    </div>
                );
            default: return <div/>;
        }
    }
}

export default Resume;
